export const EmojiPicker = ({ onEmojiSelect, onClose }: EmojiPickerProps) => {
  const { currentUser } = useAuth();
  const isPremium = currentUser?.premiumStatus === 'premium';
  const [tab, setTab] = useState<'emoji' | 'gif' | 'sticker'>('emoji');
  const [selectedCategory, setSelectedCategory] = useState<keyof typeof emojiCategories>('smileys');
  const [selectedGifCat, setSelectedGifCat] = useState<keyof typeof gifCategories>('reactions');
  const [selectedStickerCat, setSelectedStickerCat] = useState<keyof typeof stickerCategories>('cute');

  return (
    <div className="fixed inset-0 bg-black/20 flex items-end justify-center z-50" onClick={onClose}>
      <Card className="emoji-picker w-full max-w-sm mx-4 mb-4 animate-slide-up" onClick={e => e.stopPropagation()}>
        <CardContent className="p-4">
          {/* Tabs */}
          <div className="flex justify-between mb-3 gap-1">
            <Button variant={tab === 'emoji' ? 'default' : 'ghost'} size="sm" className="flex-1" onClick={() => setTab('emoji')}>Emojis</Button>
            <Button variant={tab === 'gif' ? 'default' : 'ghost'} size="sm" className="flex-1" onClick={() => setTab('gif')}>
              GIFs { !isPremium && <Lock className="inline ml-1 w-4 h-4" /> }
            </Button>
            <Button variant={tab === 'sticker' ? 'default' : 'ghost'} size="sm" className="flex-1" onClick={() => setTab('sticker')}>
              Stickers { !isPremium && <Lock className="inline ml-1 w-4 h-4" /> }
            </Button>
          </div>

          {/* Emoji Section */}
          {tab === 'emoji' && (
            <>
              <div className="flex gap-1 mb-2 flex-wrap">
                {Object.keys(emojiCategories).map(cat => (
                  <Button
                    key={cat}
                    variant={selectedCategory === cat ? 'default' : 'ghost'}
                    size="sm"
                    className="text-lg p-2 h-10 w-10"
                    onClick={() => setSelectedCategory(cat as keyof typeof emojiCategories)}
                  >
                    {emojiCategories[cat as keyof typeof emojiCategories][0]}
                  </Button>
                ))}
              </div>
              <ScrollArea className="h-48">
                <div className="grid grid-cols-8 gap-1">
                  {emojiCategories[selectedCategory].map((emoji, index) => (
                    <Button
                      key={`${selectedCategory}-${index}`}
                      variant="ghost"
                      size="sm"
                      className="text-lg p-1 h-8 w-8 hover:bg-muted"
                      onClick={() => {
                        onEmojiSelect(emoji);
                        onClose();
                      }}
                    >
                      {emoji}
                    </Button>
                  ))}
                </div>
              </ScrollArea>
            </>
          )}

          {/* GIF Section */}
          {tab === 'gif' && (
            <>
              {!isPremium ? (
                <div className="flex flex-col items-center justify-center h-48 text-center">
                  <Crown className="w-8 h-8 mb-2 text-yellow-500" />
                  <div className="font-semibold">GIFs are premium only</div>
                  <Badge variant="outline" className="mt-2">Upgrade to unlock GIFs</Badge>
                </div>
              ) : (
                <>
                  <div className="flex gap-1 mb-2 flex-wrap">
                    {Object.keys(gifCategories).map(cat => (
                      <Button
                        key={cat}
                        variant={selectedGifCat === cat ? 'default' : 'ghost'}
                        size="sm"
                        className="text-lg p-2 h-10 w-10"
                        onClick={() => setSelectedGifCat(cat as keyof typeof gifCategories)}
                      >
                        {gifCategories[cat as keyof typeof gifCategories][0].preview}
                      </Button>
                    ))}
                  </div>
                  <ScrollArea className="h-48">
                    <div className="grid grid-cols-4 gap-2">
                      {gifCategories[selectedGifCat].map((gif, index) => (
                        <Button
                          key={gif.id}
                          variant="ghost"
                          size="sm"
                          className="p-1 h-20 w-20 hover:bg-muted flex items-center justify-center"
                          onClick={() => {
                            onEmojiSelect(gif.url);
                            onClose();
                          }}
                        >
                          <img src={gif.url} alt={gif.id} className="h-16 w-16 object-contain" />
                        </Button>
                      ))}
                    </div>
                  </ScrollArea>
                </>
              )}
            </>
          )}

          {/* Sticker Section */}
          {tab === 'sticker' && (
            <>
              {!isPremium ? (
                <div className="flex flex-col items-center justify-center h-48 text-center">
                  <Crown className="w-8 h-8 mb-2 text-yellow-500" />
                  <div className="font-semibold">Stickers are premium only</div>
                  <Badge variant="outline" className="mt-2">Upgrade to unlock Stickers</Badge>
                </div>
              ) : (
                <>
                  <div className="flex gap-1 mb-2 flex-wrap">
                    {Object.keys(stickerCategories).map(cat => (
                      <Button
                        key={cat}
                        variant={selectedStickerCat === cat ? 'default' : 'ghost'}
                        size="sm"
                        className="text-lg p-2 h-10 w-10"
                        onClick={() => setSelectedStickerCat(cat as keyof typeof stickerCategories)}
                      >
                        {stickerCategories[cat as keyof typeof stickerCategories][0].preview}
                      </Button>
                    ))}
                  </div>
                  <ScrollArea className="h-48">
                    <div className="grid grid-cols-4 gap-2">
                      {stickerCategories[selectedStickerCat].map((sticker, index) => (
                        <Button
                          key={sticker.id}
                          variant="ghost"
                          size="sm"
                          className="p-1 h-20 w-20 hover:bg-muted flex items-center justify-center"
                          onClick={() => {
                            onEmojiSelect(sticker.url);
                            onClose();
                          }}
                        >
                          <img src={sticker.url} alt={sticker.id} className="h-16 w-16 object-contain" />
                        </Button>
                      ))}
                    </div>
                  </ScrollArea>
                </>
              )}
            </>
          )}
        </CardContent>
      </Card>
    </div>
  );
};